import React, { useState } from "react";
import axios from 'axios';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://127.0.0.1:8000/auth/api/login/', {
        username,
        password
      });

      // Assuming your API returns some token upon successful login
      const token = response.data.token;

      // Redirect the user or do something else upon successful login
      console.log('Login successful. Token:', token);
    } catch (error) {
      // Handle login error
      console.error('Login error:', error);
      setErrorMessage('Invalid username or password');
    }
  };

    return (
        <div className="auth-form-container">
            <h2>Login</h2>
            <form className="login-form" onSubmit={handleSubmit}>
            <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} />
                <label htmlFor="password"></label>
                <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} />
                <input class="btn login_btn" type="submit" value="Login"></input>
                {/* <a href="{% url 'core:home' %}" class="last">Login</a> */}
            </form>

            <button className="link-btn" onClick={() => props.onFormSwitch('register')}>Don't have an account? Signup here.</button>
        </div>
    )
}
export default Login
