import React, { useState } from "react";
import axios from 'axios';
import HomePage from "./Home";
import Profile from "./Profile";

const Login = (props) => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  // Function to check if the user is already logged in
  const checkLoggedIn = () => {
    const token = localStorage.getItem('token');
    const username = localStorage.getItem('username');
    if (token && username) {
      setLoggedIn(true);
    }
  };

  // Check if the user is already logged in when the component mounts
  useEffect(() => {
    checkLoggedIn();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({ ...formData, [name]: value })
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (!formData.username || !formData.password) {
        setErrorMessage('Please enter both username and password.');
        return;
      }
      const response = await axios.post('http://127.0.0.1:8000/auth/api/login/', formData);
      const { token, username } = response.data;
      localStorage.setItem('token', token);
      localStorage.setItem('username', username); // Save username in local storage
      setLoggedIn(true); // Set logged in only if successful
      setErrorMessage('');
    } catch (error) {
      console.error('Login failed:', error);
      if (error.response && error.response.status === 401) {
        setErrorMessage('Invalid username or password.');
      } else {
        setErrorMessage('An error occurred during login.');
      }
    }
  };

  if (loggedIn) {
    return <Profile />; // Redirect to Profile component after successful login
  }

  return (
    <div>
      <div className="auth-form-container">
        <form className="login-form" onSubmit={handleSubmit}>
          <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} />
          <br></br>
          <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} />
          <br></br>
          <button type="submit">Login</button>
          {errorMessage && <div className="error-message">{errorMessage}</div>}
        </form>
        <br></br>
        <button className="link-btn" onClick={() => props.onFormSwitch('register')}>Don't have an account? Signup here.</button>
      </div>
    </div>
  );
};

export default Login;
