import React, { useState } from "react";
import axios from 'axios';

const Login = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://127.0.0.1:8000/auth/api/login/', {
        username: formData.username,
        password: formData.password
      });

      // Assuming your API returns some token upon successful login
      const token = response.data.token;

      // Redirect the user or do something else upon successful login
      console.log('Login successful. Token:', token);
    } catch (error) {
      // Handle login error
      console.error('Login error:', error);
      setErrorMessage('Invalid username or password');
    }
  };

  return (
    <div className="auth-form-container">
      <h2>Login</h2>
      {errorMessage && <div style={{ color: 'red' }}>{errorMessage}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username:</label>
          <input type="text" name="username" value={formData.username} onChange={handleChange}
          />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange}
          />
        </div>
        <button type="submit">Login</button>
      </form>
      <button className="link-btn" onClick={() => props.onFormSwitch('register')}>Don't have an account? Signup here.</button>
    </div>
  );
};

export default Login
