import React, { useState, useEffect } from "react";
import axios from 'axios';
import Profile from "./Profile";
import { Navigate } from 'react-router-dom';


const Login = (props) => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('')
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({ ...formData, [name]: value })
  };

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      if (!formData.username || !formData.password) {
        setErrorMessage('Please enter both username and password.')
        return;
      }

      const response = await axios.get(`http://127.0.0.1:8000/auth/api/usernames/${formData.username}`);
      if (response.status !== 200) {
        setErrorMessage('Invalid username.');
        return;
      }

      const loginResponse = await axios.post('http://127.0.0.1:8000/auth/api/login/', {
        username: formData.username,
        password: formData.password, 
      });
      const { username } = loginResponse.data;
      localStorage.setItem('username', username);
      setErrorMessage('');
      setIsLoggedIn(true);
      return <Navigate to="/profile" />;
    } catch (error) {
      console.error('Login failed:', error);
      if (error.response && error.response.status === 401) {
        setErrorMessage('Invalid username or password.')
      } else {
        setErrorMessage('An error occurred during login.')
      }
    }

  };

  return (
    <div>
      <div className="auth-form-container">
        <form className="login-form" onSubmit={handleSubmit}>
          <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} />
          <br></br>
          <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} />
          <br></br>
          <button type="submit">Login</button>
          {errorMessage && <div className="error-message">{errorMessage}</div>}
        </form>
        <br></br>
        <button className="link-btn" onClick={() => props.onFormSwitch('register')}>Don't have an account? Signup here.</button>
      </div>
    </div>
  );
};

export default Login;
