import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import './App.css';
import Signup from "./pages/Signup";
import Login from "./pages/Login";
import HomePage from "./pages/Home";



function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentForm, setCurrentForm] = useState('login');

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const toggleForm = (formName) => {
    setCurrentForm(formName);
  };

  const PrivateRoute = ({ element, ...rest }) => {
    return isLoggedIn ? element : <Navigate to="/login" />;
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/profile" element={<PrivateRoute element={<Profile />} />} />
          <Route
            path="/login"
            element={currentForm === "login" ? <Login onFormSwitch={toggleForm} onLogin={handleLogin} /> : <Navigate to="/signup" />}
          />
          <Route
            path="/signup"
            element={currentForm === "login" ? <Navigate to="/login" /> : <Signup onFormSwitch={toggleForm} />}
          />
          {isLoggedIn ? <Route path="*" element={<Navigate to="/" />} /> : null}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
