import React, { useState } from "react";
import axios from 'axios';
import HomePage from "./Home";

const Login = (props) => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({ ...formData, [name]: value })
  };

  const handleSubmit = (e) => {
    e.preventDefault()
    const config = {
      method: 'post',
      url: 'http://127.0.0.1:8000/auth/api/login/',
      headers: {
        'Content-Type': 'application/json'
      },
      data: formData
    };

    axios.request(config)
    .then((response) => {
      console.log('Login successful:', response.data)
      setErrorMessage('')
      setLoggedIn(true);
    })
    .catch((error) => {
      console.error('Login failed:', error)
      if (error.response) {
        console.log('Response data:', error.response.data)
        console.log('Response status:', error.response.status)
        console.log('Response headers:', error.response.headers)
        setErrorMessage(error.response.data.detail || 'An error occurred during login.')
      } else {
        setErrorMessage(error.message)
      }
    });
  };

  return (
    <div className="auth-form-container">
      <h2>Login</h2>
      <div>
      {errorMessage && <div style={{ color: 'red' }}>{errorMessage}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username:</label>
          <input type="text" name="username" value={formData.username} onChange={handleChange}/>
        </div>
        <div>
          <label>Password:</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange}/>
        </div>
        <button type="submit">Login</button>
      </form>
      {errorMessage && <div className="error-message">{errorMessage}</div>}
      </div>
      <button className="link-btn" onClick={() => props.onFormSwitch('register')}>Don't have an account? Signup here.</button>
    </div>
  );
};

export default Login
