import React, { useState } from "react";
import axios from 'axios';
import HomePage from "./Home";
import { Link } from 'react-router-dom';

const Login = (props) => {
  const [formData, setFormData] = useState({
    username: props.signupData.username,
    password: props.signupData.password,
  });

  const [loggedIn, setLoggedIn] = useState(false)

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({ ...formData, [name]: value })
  };

  const handleSubmit = (e) => {
    e.preventDefault()
    const config = {
      method: 'post',
      url: 'http://127.0.0.1:8000/auth/api/login/',
      headers: {
        'Content-Type': 'multipart/form-data',
      },
      data: formData
    };

    axios.request(config)
      .then((response) => {
        console.log(JSON.stringify(response.data))
        setLoggedIn(true)
      })
      .catch((error) => {
        console.error(error)
      });
  };


  const handleSignupClick = () => {
    onFormSwitch('signup'); // Call the onFormSwitch function to switch to signup form
  };
  if (loggedIn) {
    return <HomePage />;
  }

    return (
        <div className="auth-form-container">
            <h2>Login</h2>
            <form className="login-form" onSubmit={handleSubmit}>
            <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} />
                <label htmlFor="password"></label>
                <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} />
                <input class="btn login_btn" type="submit" value="Login"></input>
                {/* <a href="{% url 'core:home' %}" class="last">Login</a> */}
            </form>

            <Link to="/signup" onClick={handleSignupClick}>Don't have an account? Signup here.</Link>
        </div>
    )
}
export default Login
