import React, { useState } from "react";
import axios from 'axios';
import HomePage from "./Home";

const Login = (props) => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({ ...formData, [name]: value })
  };

  const handleSubmit = (e) => {
    e.preventDefault()
    const config = {
      method: 'post',
      url: 'http://127.0.0.1:8000/auth/api/login/',
      headers: {
        'Content-Type': 'application/json'
      },
      data: formData
    };

    axios.request(config)
    .then((response) => {
      console.log('Login successful:', response.data)
      setErrorMessage('')
      setLoggedIn(true);
    })
    // .catch((error) => {
    //   console.error('Login failed:', error)
    //   if (error.response) {
    //     console.log('Response data:', error.response.data)
    //     console.log('Response status:', error.response.status)
    //     console.log('Response headers:', error.response.headers)
    //     setErrorMessage(error.response.data.detail || 'An error occurred during login.')
    //   } else {
    //     setErrorMessage(error.message)
    //   }
    // });
  };

  return (
<div>
      {!loggedIn ? ( 
        <div className="auth-form-container">
          <form className="login-form" onSubmit={handleSubmit}>
            <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} />
            <br></br>
            <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} />
            <br></br>
            <button type="submit">Login</button>
            {errorMessage && <div className="error-message">{errorMessage}</div>}
          </form>
          <br></br>
          <button className="link-btn" onClick={() => props.onFormSwitch('register')}>Don't have an account? Signup here.</button>
        </div>
      ) : (
        <HomePage />
      )}
    </div>


  );
};

export default Login
