import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import './App.css'
import Signup from "./pages/Signup";
import Login from "./pages/Login";
import HomePage from "./pages/Home";
import Profile from "./pages/Profile";
function App() {


  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentForm, setCurrentForm] = useState('login');

  const handleLogin = () => {
    setIsLoggedIn(true);
  }

  const RedirectToHome = () => {
    return <Navigate to="/" />;
  };
  const toggleForm = (formName) => {
    setCurrentForm(formName);
  }
  return (
    // <div>
    //   <h1>Backend Response:</h1>
    //   <p>{data}</p>
    // </div>
    <> <Router>
        <div className="App">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/login" element={currentForm === "login" ? <Login onFormSwitch={toggleForm} /> : <Navigate to="/signup" />} />
            <Route path="/signup" element={currentForm === "login" ? <Navigate to="/login" /> : <Signup onFormSwitch={toggleForm} />} />

            {isLoggedIn ? <Route path="*" element={<RedirectToHome />} /> : null}

          </Routes>
        </div>
      </Router></>
  );
}

export default App;
