import React, { useState } from 'react';
import '/Users/jenishmanandhar/djangoapi/my-app/src/Signup.css';
import axios from 'axios';

const Signup = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password1: '',
    password2: ''
  });
  const [successMessage, setSuccessMessage] = useState(''); 
  const [errorMessage, setErrorMessage] = useState(''); 

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const config = {
      method: 'post',
      url: 'http://127.0.0.1:8000/auth/api/signup/',
      headers: {
        'Content-Type': 'multipart/form-data',
        'Cookie': 'messages=.eJyLjlaKj88qzs-Lz00tLk5MT1XSMdAxMtVRckxOzi_NK1FILkpNLElNUdJRUorVGVWMpDgWAGsqZdU:1ragDh:QFtjzMc0_1EWF-VKBJNWecdyNspPM__JsKEFK2B9rbM'
      },
      data: formData
    };

    axios.request(config)
      .then((response) => {
        console.log(JSON.stringify(response.data));
        setSuccessMessage('Account created successfully');
        setErrorMessage('');
      })
      .catch((error) => {
        console.error(error);
        setErrorMessage('Error occurred during signup');
        setSuccessMessage('');
      });
  };

  return (
    <div>
    <form className="signup-form" onSubmit={handleSubmit}>
      <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} />
      <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} />
      <input type="password" name="password1" placeholder="Password" value={formData.password1} onChange={handleChange} />
      <input type="password" name="password2" placeholder="Confirm Password" value={formData.password2} onChange={handleChange} />
      <button type="submit">Sign Up</button>
    </form>
    {successMessage && <div className="success-message">{successMessage}</div>}
    {errorMessage && <div className="error-message">{errorMessage}</div>}
    </div>
  );
};
